mapper.select:
SELECT 

FROM hc_task a

WHERE a.id = #{id}

SELECT 

FROM hc_task a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.type = #{type}


		AND a.task_status = #{taskStatus}


		AND a.create_by = #{createBy.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM hc_task a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT a.id AS "id", a.title AS "title", a.type AS "type", a.count AS "count", a.content AS "content", a.create_by AS "createBy.id", a.update_by AS "updateBy.id", a.task_status AS "taskStatus", a.success_number AS "successNumber" FROM hc_task a
a.del_flag = '0' AND id >= (SELECT floor(RAND() * (SELECT MAX(id) FROM hc_task))) AND a.task_status = '0' OR (a.task_status = '2' AND a.type = '3')
ORDER BY id LIMIT 1;
